package patterns.design.chapter7.facadepattern;

public class PopcornPopper {
	public PopcornPopper() {
		// TODO Auto-generated constructor stub
	}
	
	public void on(){
		System.out.println("Popper is on");
	}
	
	public void off(){
		System.out.println("Popper is off");
	}
	
	public void pop(){
		System.out.println("Popcorn has been popped");
	}
}
